// import qs from 'query-string'

class Location {
  static hash(hash) {
    if (hash !== undefined) {
      window.location.hash = hash
    }

    return window.location.hash.substr(1, window.location.hash.length)
  }

  static qs() {
    // const router = useRouter()
        // return qs.parse(window.location.search)
    return {}
  }

  static pathname(router) {
    console.log(router)
    if (router) {
      return router.pathname.substr(1).split('/')[0] || Location.links[0].link
    } else {
      return Location.links[0].link
    }
  }
}

Location.links = [{
  link: 'home',
  label: 'Home'
}, {
  link: 'work',
  label: 'Work'
}, {
  link: 'skills',
  label: 'Skills'
}, {
  link: 'experiences',
  label: 'Experiences'
}]

export default Location
