import React, {Component} from 'react'
import {withRouter} from 'next/router'
// import PropTypes from 'prop-types'

import LandingPage from '../component/section/Landing'
import ProjectListPage from '../component/section/ProjectList'
import SkillPage from '../component/section/Skill'
import ExperiencePage from '../component/section/Experience'

import Scroller from '../component/Scroller'
import Footer from '../component/footer/Footer'
import Header from '../component/header/Header'

import Location from '../helpers/Location'

import './MainScrollPage.css'

class MainScrollPage extends Component {

  constructor() {
    super()
    this.pageIds = []
    this.onScroll = this.onScroll.bind(this)
    this.container = undefined
    // this.container = document.getElementsByTagName('body')[0]
  }

  componentDidMount() {
    this.pageIds = Location.links.map(l => l.link)
    this.container = window
  }

  onScroll(index) {
    const {history} = this.props

    history.push(`/${this.pageIds[index]}`)
  }

  render() {
    const idx = this.pageIds.indexOf(Location.pathname(this.props.router))

    return (
      <>
        <Header></Header>
        <Scroller className="main-scroller" onScroll={this.onScroll} index={idx === -1 ? 0 : idx} container={this.container}>
          <div id="scroll-home-container" className="children">
            <LandingPage />
          </div>
          <div id="scroll-work-container" className="children">
            <ProjectListPage />
          </div>
          <div id="scroll-skills-container" className="children">
            <SkillPage />
          </div>
          <div id="scroll-experiences-container" className="children">
            <ExperiencePage />
            <Footer />
          </div>
        </Scroller>
      </>
    )
  }
}

MainScrollPage.propTypes = {
}

export default withRouter(MainScrollPage)
